
public class RectangleAreaCalculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double length = 23.5;
		double width = 14.7;
		double area = 0;
		String message = "The area of the rectangle is: ";
		
		area = length * width;
		message = message + area;
		System.out.println(message);
	}

}
